import React, { useContext } from 'react';
import { useForm } from 'react-hook-form';
import { useLocation, useNavigate, Link } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './LoginPage.css';
import { AuthContext } from '../../contexts/AuthContext';

const LoginPage = () => {
  const { register, handleSubmit } = useForm();
  const navigate = useNavigate();
  const location = useLocation();
  const { login } = useContext(AuthContext);

  const handleLoginResponse = async (response, email) => {
    if (response.ok) {
      const result = await response.json();
      const accessToken = result.data.accessToken || result.data;
      login(accessToken, email);
      toast.success('Login successful! Redirecting...', {
        onClose: () => navigate('/'),
      });
    } else {
      const errorData = await response.json();
      console.error('Login failed:', errorData);
      toast.error('Login failed: ' + errorData.message);
    }
  };

  const onSubmit = async (data) => {
    try {
      const response = await fetch('https://cp.dhytv.com/v1/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: data.email,
          password: data.password,
        }),
      });

      await handleLoginResponse(response, data.email);
    } catch (error) {
      console.error('Error during login:', error);
      toast.error('An error occurred during login. Please try again.');
    }
  };

  const handleGoogleLoginClick = () => {
    const googleLoginUrl = 'https://cp.dhytv.com/v1/google/sign-in';
    const width = 500;
    const height = 600;
    const left = window.screen.width / 2 - width / 2;
    const top = window.screen.height / 2 - height / 2;

    const popup = window.open(
      googleLoginUrl,
      'googleLogin',
      `width=${width},height=${height},top=${top},left=${left}`
    );

    window.addEventListener('message', (event) => {
      // Ensure the message is from your domain
      if (event.origin === window.location.origin) {
        const { accessToken, email } = event.data;
        if (accessToken) {
          login(accessToken, email);
          toast.success('Login successful! Redirecting...', {
            onClose: () => navigate('/'),
          });
        }
      }
    });
  };

  return (
    <div className="login-page">
      <ToastContainer />
      {/* Google Login Button */}
      <div className="google-login">
        <button onClick={handleGoogleLoginClick} className="nav-button">
          Login with Google
        </button>
      </div>
      <h1 className="artistic-word">Login</h1>

      <form onSubmit={handleSubmit(onSubmit)}>
        <div>
          <label>Email:</label>
          <input
            type="text"
            defaultValue={location.state?.email || ''}
            {...register('email', { required: true })}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            defaultValue={location.state?.password || ''}
            {...register('password', { required: true })}
          />
        </div>
        <button type="submit" className="nav-button">Login</button>
      </form>



      <p>Don't have a username? <Link to="/register">Register here</Link></p>
    </div>
  );
};

export default LoginPage;
