import React, { useContext, useEffect, useCallback } from 'react';
import { useForm } from 'react-hook-form';
import { useLocation, useNavigate, Link } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './LoginPage.css';
import { AuthContext } from '../../contexts/AuthContext';

const LoginPage = () => {
  const { register, handleSubmit } = useForm();
  const navigate = useNavigate();
  const location = useLocation();
  const { login } = useContext(AuthContext);

  const handleLoginResponse = async (response, email) => {
    if (response.ok) {
      const result = await response.json();
      const accessToken = result.data?.accessToken || result.data?.token || result.token;
      if (accessToken) {
        login(accessToken, email);
        toast.success('Login successful! Redirecting...', {
          onClose: () => navigate('/'),
        });
      } else {
        console.error('Login failed: No token received.');
        toast.error('Login failed: No token received.');
      }
    } else {
      const errorData = await response.json();
      console.error('Login failed:', errorData);
      toast.error('Login failed: ' + errorData.message);
    }
  };

  const onSubmit = async (data) => {
    try {
      const response = await fetch('https://cp.dhytv.com/v1/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: data.email,
          password: data.password,
        }),
      });
      await handleLoginResponse(response, data.email);
    } catch (error) {
      console.error('Error during login:', error);
      toast.error('An error occurred during login. Please try again.');
    }
  };

  let googlePopup = null;

  const handleGoogleLoginClick = () => {
    console.log('Opening Google login popup');
    googlePopup = window.open(
      'https://cp.dhytv.com/v1/google/sign-in',
      'google-login',
      `width=600,height=600,top=${window.screen.height / 2 - 300},left=${window.screen.width / 2 - 300}`
    );
    if (!googlePopup || googlePopup.closed || typeof googlePopup.closed === 'undefined') {
      console.error('Popup was blocked');
      toast.error('Popup was blocked. Please allow popups for this site.');
    } else {
      console.log('Popup opened successfully');
    }
  };

  const handleMessage = useCallback(async (event) => {
    console.log('Message received:', event);
    console.log('Message origin:', event.origin);
    console.log('Expected origin:', window.location.origin);

    console.log('Message data:', event.data);

    try {
      const responseData = JSON.parse(event.data);
      console.log('Parsed response data:', responseData);

      if (responseData.code === 200 && responseData.data) {
        const token = responseData.data.replace('Bearer ', '');
        console.log('Token extracted:', token);

        // Use the token for authentication
        login(token);
        toast.success('Google login successful! Redirecting...');
        navigate('/');

        // Check if popup is still open and close it if necessary
        if (window.opener) {
          window.close();
        }
      } else {
        console.error('Login failed:', responseData.message);
        toast.error('Login failed: ' + responseData.message);
      }
    } catch (error) {
      console.error('Error processing message:', error);
      toast.error('An error occurred during login. Please try again.');
    }
  }, [login, navigate]);

  useEffect(() => {
    console.log('Adding message event listener');
    window.addEventListener('message', handleMessage);
    return () => {
      console.log('Removing message event listener');
      window.removeEventListener('message', handleMessage);
      if (googlePopup && !googlePopup.closed) {
        googlePopup.close();
      }
    };
  }, [handleMessage]);

  return (
    <div className="back-ground">
      <div className="login-page">
        <ToastContainer />
        <h1 className="artistic-word">Login</h1>

        <form onSubmit={handleSubmit(onSubmit)}>
          <div>
            <label>Email:</label>
            <input
              type="text"
              defaultValue={location.state?.email || ''}
              {...register('email', { required: true })}
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="password"
              defaultValue={location.state?.password || ''}
              {...register('password', { required: true })}
            />
          </div>
          <button type="submit" className="nav-button">Login</button>
        </form>

        <div className="separator">
          <hr className="separator-line" />
          <span className="separator-text">or</span>
          <hr className="separator-line" />
        </div>

        <div className="google-login">
          <button onClick={handleGoogleLoginClick} className="nav-button">
            Login with Google
          </button>
        </div>

        <p>Don't have a username? <Link to="/register">Register here</Link></p>
      </div>
    </div>
  );
};

export default LoginPage;
