import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import './SearchPage.css';

const SearchPage = () => {
    const navigate = useNavigate();
    const { isLoggedIn, userEmail } = useAuth();
    const [searchWord, setSearchWord] = useState('');
    const [definedWord, setDefinedWord] = useState('');
    const [definedNote, setDefinedNote] = useState('');
    const [searchResult, setSearchResult] = useState(null);
    const [showLinks, setShowLinks] = useState(false);
    const [searchedWords, setSearchedWords] = useState([]);
    const [userDictionary, setUserDictionary] = useState(new Set()); // Using Set for uniqueness
    const [dictionaryOption, setDictionaryOption] = useState('English-Chinese'); // State for dropdown


    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            console.error('No token found, please log in.');
        }
    }, [navigate]);

    const handleInputSubmit = (event) => {
        event.preventDefault();  // Prevent the default form submit action

        // Create a dictionary with the word and note
        const newEntry = { word: definedWord, note: definedNote };

        // Append the new entry to the existing search results
        setSearchResult((prevResults) => [...prevResults, newEntry]);

        // Clear the input fields
        setDefinedWord('');
        setDefinedNote('');
    };



    const handleSearchSubmit = async (event) => {
        event.preventDefault();
        // Retrieve the token from localStorage
        const token = localStorage.getItem('token');


        try {
            const response = await fetch(`https://cp.dhytv.com/v1/dicts/word/${searchWord}`, {
                method: 'GET',
                headers: {
                    'Authorization': `${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                console.error('Server Error:', errorData.message || 'Failed to fetch word data');
                throw new Error(errorData.message || 'Failed to fetch word data');
            }
            const resultData = await response.json();
            console.log(resultData)

            setSearchResult(resultData);
            // Add search word and its result to the session's searched words
            setSearchedWords(prevWords => [...prevWords, { word: searchWord, translation: resultData.data.translation }]);

        } catch (error) {
            console.error('Error fetching word data:', error);
            setSearchResult(null);
        }
    };

    const toggleLinks = () => {
        setShowLinks(!showLinks);
    };

    const toggleDictionary = (word) => {
        setUserDictionary((prevDictionary) => {
            const newDictionary = new Set(prevDictionary);
            if (newDictionary.has(word)) {
                newDictionary.delete(word); // Remove word if already in dictionary
            } else {
                newDictionary.add(word); // Add word to dictionary
            }
            return newDictionary;
        });
    };

    const handleDictionaryChange = (event) => {
        setDictionaryOption(event.target.value);
    };

    return (
        <div className="search-page-background">

            <div className="search-dict">
                <p>Current Dictionary</p>
                <select value={dictionaryOption} onChange={handleDictionaryChange}>
                    <option value="English-Chinese">English-Chinese</option>
                    {/* Add more options here if needed */}
                </select>
            </div>

            <div className="search-left-right">
                <div className="search-left-panel">
                    <section className="search-section-box">
                        <h2 className="search-artistic-word">Word Search</h2>

                        <form onSubmit={handleSearchSubmit}>
                            <input
                                type="text"
                                placeholder="Enter a word to search"
                                value={searchWord}
                                onChange={(e) => setSearchWord(e.target.value)}
                            />
                            <button className="search-nav-button" type="submit">Search</button>
                        </form>


                        <form onSubmit={handleInputSubmit}>
                            <input
                                type="text"
                                placeholder="Enter your word"
                                value={definedWord}
                                onChange={(e) => setDefinedWord(e.target.value)}
                            />

                            <input
                                type="text"
                                placeholder="Enter your note"
                                value={definedNote}
                                onChange={(e) => setDefinedNote(e.target.value)}
                            />

                            <button className="search-nav-button" type="submit">Search</button>
                        </form>


                        {searchResult && searchResult.data && (

                            <div className="search-result-box">

                                <div className="word-button-group">
                                    <p><strong>Word:</strong> {searchResult.data.word}</p>
                                    <button
                                        className="audio-icon-button"
                                        onClick={() => document.getElementById(`audio-${searchResult.data.word}`).play()}
                                        style={{ padding: '0px', marginLeft: '2px', cursor: 'pointer', border: 'none', background: 'none' }}
                                    >
                                        🔊 {/* Use an actual icon or SVG if desired */}
                                    </button>
                                    <audio id={`audio-${searchResult.data.word}`} src={`https://dict.youdao.com/dictvoice?audio=${searchResult.data.word}`} />
                                </div>

                                <p style={{ margin: 0 }}><strong>Translation:</strong> {searchResult.data.translation}</p>
                                <p><strong>Phonetic:</strong> {searchResult.data.phonetic}</p>


                                {/* <div onClick={toggleLinks} style={{ cursor: 'pointer', color: 'blue', textDecoration: 'underline' }}>
                                    {showLinks ? '▼ Hide Links' : '► Show Links'}
                                </div> */}
                                {/* {showLinks && (
                                    <ul>
                                        {searchResult.data.links.map((link, index) => (
                                            <li key={index}><a href={link} target="_blank" rel="noopener noreferrer">{link}</a></li>
                                        ))}
                                    </ul>
                                )} */}
                            </div>
                        )}
                    </section>
                </div>

                <div className="search-right-panel">
                    <section className="search-section-box">
                        <h2 className="search-artistic-word">Today's Search</h2>
                        <div className="searched-words-container">
                            {searchedWords.map(({ word, translation }, index) => (
                                <div className="word-card" key={index}>
                                    <p><strong>Word:</strong> {word}</p>
                                    <p><strong>Translation:</strong> {translation}</p>
                                    <button
                                        className="dictionary-star"
                                        onClick={() => toggleDictionary(word)}
                                        style={{ color: userDictionary.has(word) ? 'gold' : 'gray' }}
                                    >
                                        ★
                                    </button>
                                </div>
                            ))}
                        </div>
                    </section>
                </div>
            </div>
        </div>
    );
};

export default SearchPage;
