import React, { useContext } from 'react';
import { useForm } from 'react-hook-form';
import { useLocation, useNavigate, Link } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './LoginPage.css';
import { AuthContext } from '../../contexts/AuthContext'; // Import AuthContext

const LoginPage = () => {
  const { register, handleSubmit } = useForm();
  const navigate = useNavigate();
  const location = useLocation();
  const { login } = useContext(AuthContext); // Get login function from AuthContext

  const handleLoginResponse = async (response, email) => {
    if (response.ok) {
      const result = await response.json();
      const accessToken = result.data.accessToken || result.data;

      // Call the login function from AuthContext to update the context
      login(accessToken, email);

      // Show success message
      toast.success('Login successful! Redirecting...', {
        onClose: () => navigate('/'), // Redirect after the toast closes
      });
    } else {
      const errorData = await response.json();
      console.error('Login failed:', errorData);
      toast.error('Login failed: ' + errorData.message);
    }
  };

  const onSubmit = async (data) => {
    try {
      const response = await fetch('https://cp.dhytv.com/v1/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: data.email,
          password: data.password,
        }),
      });

      await handleLoginResponse(response, data.email);
    } catch (error) {
      console.error('Error during login:', error);
      toast.error('An error occurred during login. Please try again.');
    }
  };

  const handleGoogleLoginClick = () => {
    window.location.href = 'https://cp.dhytv.com/v1/google/sign-in'; // Redirect to your backend's Google sign-in endpoint
  };

  React.useEffect(() => {
    // Handle response from Google login redirect
    const urlParams = new URLSearchParams(window.location.search);
    const googleToken = urlParams.get('token');

    if (googleToken) {
      fetch('https://cp.dhytv.com/v1/google/validate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ token: googleToken }),
      })
        .then(response => handleLoginResponse(response))
        .catch(error => {
          console.error('Error during Google login:', error);
          toast.error('An error occurred during Google login. Please try again.');
        });
    }
  }, []); // Empty dependency array to run only on component mount

  return (
    <div className="login-page">
      <ToastContainer /> {/* Toast container for notifications */}
      <h1 className="artistic-word">Login</h1>

      <form onSubmit={handleSubmit(onSubmit)}>
        <div>
          <label>Email:</label>
          <input
            type="text"
            defaultValue={location.state?.email || ''}
            {...register('email', { required: true })}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            defaultValue={location.state?.password || ''}
            {...register('password', { required: true })}
          />
        </div>
        <button type="submit" className="nav-button">Login</button>
      </form>

      {/* Google Login Button */}
      <div className="google-login">
        <button onClick={handleGoogleLoginClick} className="nav-button">
          Login with Google
        </button>
      </div>

      <p>Don't have a username? <Link to="/register">Register here</Link></p>
    </div>
  );
};

export default LoginPage;
