import React from 'react';
import { useForm } from 'react-hook-form';
import { useLocation, useNavigate } from 'react-router-dom';
import './LoginPage.css';

const LoginPage = () => {
  const { register, handleSubmit } = useForm();
  const navigate = useNavigate(); // Now we'll use navigate
  const location = useLocation();

  const onSubmit = async (data) => {
    try {
      const response = await fetch('https://your-api-endpoint/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          password: data.password,
        }),
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Login successful:', result);

        // Store the token or handle the response as needed
        localStorage.setItem('token', result.token);

        // Redirect to the home page or another protected page
        navigate('/');
      } else {
        const errorData = await response.json();
        console.error('Login failed:', errorData);
        alert('Login failed: ' + errorData.message);
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('An error occurred during login. Please try again.');
    }
  };

  return (
    <div className="login-page">
      <h1>Login</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            defaultValue={location.state?.username || ''}
            {...register('username', { required: true })}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            defaultValue={location.state?.password || ''}
            {...register('password', { required: true })}
          />
        </div>
        <button type="submit" className="nav-button">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
