import React, { useEffect, useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import './SearchPage.css';

const SearchPage = () => {
    const { isLoggedIn, userEmail } = useAuth();
    const [searchWord, setSearchWord] = useState('');
    const [searchResult, setSearchResult] = useState(null);

    const handleSearchSubmit = async (event) => {
        event.preventDefault();

        const token = localStorage.getItem('token'); // Fetch token from local storage

        if (!token) {
            console.error('No token found, please log in.');
            return;
        }

        try {
            const response = await fetch(`https://cp.dhytv.com/v1/dicts/word/${searchWord}`, {
                method: 'GET',
                headers: {
                    'Authorization': `${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                console.error('Server Error:', errorData.message || 'Failed to fetch word data');
                throw new Error(errorData.message || 'Failed to fetch word data');
            }

            const resultData = await response.json();
            console.log(resultData);
            setSearchResult(resultData);
        } catch (error) {
            console.error('Error fetching word data:', error);
            setSearchResult(null);
        }
    };

    return (
        <div className="search-page-main-content">
            <div className="search-left-panel">
                <section className="search-section-box">
                    <h2 className="search-artistic-word">Word Search</h2>
                    <form onSubmit={handleSearchSubmit}>
                        <input
                            type="text"
                            placeholder="Enter a word to search"
                            value={searchWord}
                            onChange={(e) => setSearchWord(e.target.value)}
                        />
                        <button className="search-nav-button" type="submit">Search</button>
                    </form>

                    {searchResult && (
                        <div>
                            <h3>Search Result for "{searchWord}":</h3>
                            <p>{JSON.stringify(searchResult)}</p>
                        </div>
                    )}
                </section>
            </div>
        </div >
    );
};

export default SearchPage;
