import React, { useState, useEffect } from 'react';
import './RecitePage.css';

const RecitePage = () => {
    const [words, setWords] = useState([
        { word: 'Hello', translation: '你好' },
        { word: 'Goodbye', translation: '再见' },
        { word: 'Thank you', translation: '谢谢' },
        // Add more words as needed
    ]);

    const [currentIndex, setCurrentIndex] = useState(0);
    const [showTranslation, setShowTranslation] = useState(false);

    const handleNextWord = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % words.length);
        setShowTranslation(false);
    };

    const toggleTranslation = () => {
        setShowTranslation(!showTranslation);
    };

    return (
        <div className="recite-page">
            <div className="recite-card">
                <h2 className="recite-word">{words[currentIndex].word}</h2>
                {showTranslation && (
                    <p className="recite-translation">{words[currentIndex].translation}</p>
                )}
                <div className="recite-buttons">
                    <button onClick={toggleTranslation}>
                        {showTranslation ? 'Hide Translation' : 'Show Translation'}
                    </button>
                    <button onClick={handleNextWord}>Next Word</button>
                </div>
            </div>
        </div>
    );
};

export default RecitePage;
