import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Stage, Layer, Shape } from 'react-konva';
import './MainPage.css';

const MainPage = () => {
  const navigate = useNavigate();

  const handleButtonClick = () => {
    navigate('/function');
  };

  return (
    <div className="main-content">
      {/* Container for text and buttons */}
      <div className="text-and-buttons">
        <h1>Welcome to</h1>
        <div className="artistic-word">
          <div className="scattered-letters">
            <span>C</span>
            <span>o</span>
            <span>l</span>
            <span>l</span>
            <span>e</span>
            <span>c</span>
            <span>t</span>
          </div>

          <span> </span>
          <span className="slightly-unaligned" id="letter-p">P</span>
          <span className="slightly-unaligned" id="letter-i">i</span>
          <span className="slightly-unaligned" id="letter-e">e</span>
        </div>
        <p>Your ultimate tool for collecting, managing, and mastering words and phrases from around the world.</p>

        <div className="mud-button-section">
          <div className="button-group">
            <button onClick={handleButtonClick}>Search</button>
            <button onClick={handleButtonClick}>Recite</button>
            <button onClick={handleButtonClick}>Statistics</button>
          </div>
          <div className="button-group">
            <button onClick={handleButtonClick}>Download Customised Dictionary</button>
          </div>
        </div>
      </div>

      {/* Canvas for triangle drawing */}
      <Stage width={window.innerWidth} height={window.innerHeight} className="stage-canvas">
        <Layer>
          {/* Left Triangle Shape */}
          <Shape
            sceneFunc={(context, shape) => {
              context.beginPath();
              context.moveTo(-100, 100);
              context.lineTo(100000, 200);
              context.lineTo(0, 300);
              context.closePath();
              context.fillStrokeShape(shape);
            }}
            fill="transparent"
            stroke="black"
            strokeWidth={3}
            x={0}
            y={0}
            rotation={-30}
          />

          {/* Right Triangle Shape */}
          <Shape
            sceneFunc={(context, shape) => {
              context.beginPath();
              context.moveTo(100, 0);
              context.lineTo(200, 300);
              context.lineTo(700, 300);
              context.closePath();
              context.fillStrokeShape(shape);
            }}
            fill="transparent"
            stroke="black"
            strokeWidth={3}
            x={window.innerWidth - 300}
            y={0}
            rotation={30}
          />
        </Layer>
      </Stage>
    </div>
  );
};

export default MainPage;
