import React, { useContext } from 'react';
import { useForm } from 'react-hook-form';
import { useLocation, useNavigate, Link } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './LoginPage.css';
import { AuthContext } from '../../contexts/AuthContext'; // Import AuthContext

const LoginPage = () => {
  const { register, handleSubmit } = useForm();
  const navigate = useNavigate();
  const location = useLocation();
  const { login } = useContext(AuthContext); // Get login function from AuthContext

  const onSubmit = async (data) => {
    try {
      const response = await fetch('https://cp.dhytv.com/v1/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: data.email,
          password: data.password,
        }),
      });

      if (response.ok) {
        const result = await response.json();

        // Call the login function from AuthContext to update the context
        login(result.data.accessToken, data.email);

        // Show success message
        toast.success('Login successful! Redirecting...', {
          onClose: () => navigate('/'), // Redirect after the toast closes
        });
      } else {
        const errorData = await response.json();
        console.error('Login failed:', errorData);
        toast.error('Login failed: ' + errorData.message);
      }
    } catch (error) {
      console.error('Error during login:', error);
      toast.error('An error occurred during login. Please try again.');
    }
  };

  const handleGoogleLoginSuccess = (response) => {
    // You should verify and handle the Google response token on the backend
    const tokenId = response.credential;
    // Example: send tokenId to backend to verify and get user info
    fetch('https://cp.dhytv.com/v1/auth/google', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ tokenId }),
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          login(data.accessToken, data.user.email);
          toast.success('Google login successful! Redirecting...', {
            onClose: () => navigate('/'),
          });
        } else {
          toast.error('Google login failed: ' + data.message);
        }
      })
      .catch(error => {
        console.error('Error during Google login:', error);
        toast.error('An error occurred during Google login. Please try again.');
      });
  };

  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      <div className="login-page">
        <ToastContainer /> {/* Toast container for notifications */}
        <h1 className="artistic-word">Login</h1>

        <form onSubmit={handleSubmit(onSubmit)}>
          <div>
            <label>Email:</label>
            <input
              type="text"
              defaultValue={location.state?.email || ''}
              {...register('email', { required: true })}
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="password"
              defaultValue={location.state?.password || ''}
              {...register('password', { required: true })}
            />
          </div>
          <button type="submit" className="nav-button">Login</button>
        </form>

        {/* Google Login Button */}
        <div className="google-login">
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onError={() => {
              toast.error('Google login failed. Please try again.');
            }}
          />
        </div>

        <p>Don't have a username? <Link to="/register">Register here</Link></p>
      </div>
    </GoogleOAuthProvider>
  );
};

export default LoginPage;
