const onSubmit = async (data) => {
    try {
        const response = await fetch('https://cp.dhytv.com/v1/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                email: data.username, // Assuming you're using the email field for login
                password: data.password,
            }),
        });

        if (response.ok) {
            const result = await response.json();
            console.log('Login successful:', result);

            // Store the token or handle the response as needed
            localStorage.setItem('token', result.token);

            // Redirect to the home page or another protected page
            navigate('/');
        } else {
            const errorData = await response.json();
            console.error('Login failed:', errorData);
            alert('Login failed: ' + errorData.message);
        }
    } catch (error) {
        console.error('Error during login:', error);
        alert('An error occurred during login. Please try again.');
    }
};
