// FunctionPage.js
import React, { useEffect, useState } from 'react';
import { useAuth } from '../../contexts/AuthContext'; // Adjust the import path as needed
import CalendarHeatmap from 'react-calendar-heatmap';
import 'react-calendar-heatmap/dist/styles.css'; // Import default styles for the heatmap

const FunctionPage = () => {
  const { userEmail } = useAuth(); // Access the user's email from the AuthContext
  const [heatmapData, setHeatmapData] = useState([]); // State to store heatmap data

  // Fetch heatmap data from the API when the component mounts
  useEffect(() => {
    const fetchHeatmapData = async () => {
      try {
        const response = await fetch('https://cp.dhytv.com/v1/stats/heatmap');
        const data = await response.json();

        // Process the data to match the format required by react-calendar-heatmap
        const processedData = data.map((item) => ({
          date: item.date, // Assuming 'date' is a key in the fetched data
          count: item.count, // Assuming 'count' is a key in the fetched data
        }));

        setHeatmapData(processedData);
      } catch (error) {
        console.error('Error fetching heatmap data:', error);
      }
    };

    fetchHeatmapData();
  }, []);

  return (
    <div>
      {/* Welcome message with user's name or email */}
      <div>
        <h1>Welcome, {userEmail || 'User'}!</h1>
      </div>

      {/* Section for English-Chinese vocabulary */}
      <section>
        <h2>English-Chinese Vocabulary</h2>
        {/* ... Your vocabulary section goes here ... */}
      </section>

      {/* Section for vocabulary progress */}
      <section>
        <h2>Progress</h2>
        {/* ... Your progress section goes here ... */}
      </section>

      {/* Heatmap Section */}
      <section>
        <h2>Learning Heatmap</h2>
        <CalendarHeatmap
          startDate={new Date('2024-01-01')}
          endDate={new Date('2024-12-31')}
          values={heatmapData}
          classForValue={(value) => {
            if (!value) {
              return 'color-empty';
            }
            return `color-github-${Math.min(value.count, 4)}`; // Customize the color based on count
          }}
        />
      </section>
    </div>
  );
};

export default FunctionPage;
